mapper.select:
select count(*)
from follow
where toUser = #{toUser}

select count(*)
from follow
where fromUser = #{fromUser}

SELECT *
FROM user_info
WHERE ur_id IN (
    SELECT fromUser
    FROM follow
    WHERE toUser = #{toUser}
)
LIMIT #{offset},#{pageSize}

SELECT *
FROM user_info
WHERE ur_id IN (
    SELECT toUser
    FROM follow
    WHERE fromUser = #{fromUser}
)
    LIMIT #{offset},#{pageSize}

mapper.delete:
DELETE FROM follow
WHERE fromUser=#{fromUser}
AND toUser = #{toUser}
